INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Goat_39', 3, 'Ukweli wa kuvutia kushirikishana, kuhusu rafiki anayelia', 'assets/topic/goatbleeting_10.jpg', 'Mtoto wa Mbuzi anaitwa Kimbalelo. Lakini ukweli ni kwamba Mbuzi ajifunguapo huchukuliwa kama "utani". Mbuzi hana meno katika taya yake ya juu. Sasa hiki ni kichekesho. Sijui ingekuaje kama Binadamu angekua hana meno kwenye taya ya juu kama mbuzi. Mbuzi anaweza kupanda miti na kuruka urefu wa futi 5. Hodari sana!!. Mbuzi wana milio tofauti kutoka kanda moja hadi kanda nyingine. Hii ina maana milion ya mbuzi ya nchi moja inatofautiana na milio ya mbuzi wa nchi nyingine. Mfanano kiasi na sisi binadamu. Mbuzi hawapendi kulowa na kucheza kwenye mvua. Mbuzi wana matumbo manne yanayojulikana kama RUMEN. Hii huwasaidia kwenye mmeng''enyo kamilifu wa chakula. Sasa hii ndo siri ya malisho yao endelevu.', NULL);
