INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Zebra', 2, 'Zebra', 'assets/topic/zebra.svg', 'With their signature black and white stripes who cannot recognize a zebra! Zebras are single-hoofed animals that are native of Africa. Zebras belong to horse family and are very closely realted to horses and donkeys. Zebras are now in the endangered animals list with only 2500 - 2700 population in the world.', 'FFCFB5DD');
