INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tomatoes_11', 3, 'Health benefits of tomato', 'assets/topic/tomato_health.jpg', 'Tomatoes are the major dietry source. It has many health benifts. Eating tomatoes reduce the risk of heart disease and cancer. Tomatoes are good for eye health, skin, constipation and diabetes. Most importantly, tomatoes are tasty.', NULL);
