INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403', 2, 'Adie and Adhoch', 'assets/topic/asb/14705.png', 'Christine Nyangate
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_3', 3, NULL, 'assets/topic/asb/9066.png', 'There was a man who had two wives. The wives gave birth to two daughters, Adie and Adhoch. The step sisters loved each other very much.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_4', 3, NULL, 'assets/topic/asb/13839.png', 'Adie was hard working, neat, kind and well behaved. Adhoch, on the other hand, was lazy, unkind and untidy.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_5', 3, NULL, 'assets/topic/asb/13843.png', 'Teachers loved Adie. She got many presents for her hard work. Adhoch did not receive any. Adhoch''s mother was jealous of Adie.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_6', 3, NULL, 'assets/topic/asb/13837.png', 'Adie''s mother became sick and died. Adie''s stepmother did not love her as her mother did.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_7', 3, NULL, 'assets/topic/asb/13838.png', 'Adhoch''s mother made Adie do all the work. She did not have time to get ready for school. Adhoch studied and played when Adie was busy working.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_8', 3, NULL, 'assets/topic/asb/8023.png', 'Adie still did well in school. She passed her exams and received more presents. This made Adhoch''s mother very very angry.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_9', 3, NULL, 'assets/topic/asb/15112.png', 'One day, Adhoch''s mother asked Adie to go with her to the lake to fetch water. When they got to the lake, Adhoch''s mother told her to go deeper into the lake to fetch clean water. Big waves rose and Adie drowned.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_10', 3, NULL, 'assets/topic/asb/13697.png', 'The stepmother went back home. Adhoch asked where her sister was. Her mother said, "Adie is at the lake with her friends. She will come home later."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_11', 3, NULL, 'assets/topic/asb/1099.png', 'When night fell, Adhoch went to look for her sister at the lake. She called her name, "Adie! Where are you?" Adhoch heard, "I am in the lake. Mother drowned me."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_12', 3, NULL, 'assets/topic/asb/1195.png', 'Adhoch walked into the lake calling her sister. "Adie! Where are you?" She heard the same reply, "I am in the lake. Mother drowned me."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_13', 3, NULL, 'assets/topic/asb/15112.png', 'Adhoch got deep into the lake. She drowned in the same way as Adie.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_14', 3, NULL, 'assets/topic/asb/15011.png', 'Adhoch''s mother was sad. She lost Adhoch because she was jealous of Adie. She had to do all the work by herself.', NULL);
