INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218', 2, 'Adun, the Beautiful', 'assets/topic/asb/13337.png', 'Taiwo Ẹhinẹni
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_3', 3, NULL, 'assets/topic/asb/13337.png', 'Adun was very beautiful. All the men in her village wanted to marry her.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_4', 3, NULL, 'assets/topic/asb/13338.png', 'One market day,  a man borrowed legs from another man,  and arms from another,  and a body from a third man.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_5', 3, NULL, 'assets/topic/asb/13339.png', 'This man wanted Adun and was determined to marry her. Adun was pleased by his appearance. She wanted to be with him.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_6', 3, NULL, 'assets/topic/asb/13340.png', 'As Adun and the man journeyed to his place,  the owner of the legs took the legs back. Then the owner of the arms took his arms back.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_7', 3, NULL, 'assets/topic/asb/13341.png', 'Only the head was left,  and the head went on. Adun was scared but she did not run away.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_8', 3, NULL, 'assets/topic/asb/13342.png', 'The following morning,  before the head went to his farm,  he spoke to Tortoise. "If Adun tries to escape,  blow this horn to tell me," said the head.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_9', 3, NULL, 'assets/topic/asb/13343.png', 'After the head left,  Adun packed her things. Then she began to run away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_10', 3, NULL, 'assets/topic/asb/13344.png', 'Seeing her,  Tortoise blew the horn,  "Head,  head,  Adun is about to run away," puffed Tortoise.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_11', 3, NULL, 'assets/topic/asb/13345.png', 'The head went up to Adun and said,  "Where do you think you are going?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_12', 3, NULL, 'assets/topic/asb/13346.png', 'Eventually Adun consulted a diviner about what to do.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_13', 3, NULL, 'assets/topic/asb/13347.png', 'Adun followed the diviner''s instructions. She put the oily cakes into the horn.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_14', 3, NULL, 'assets/topic/asb/13349.png', 'The following day,  she took her things and began to run away again.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_15', 3, NULL, 'assets/topic/asb/13348.png', 'Tortoise ate,  ate and ate.
