INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925', 2, 'Ah! Football!', 'assets/topic/asb/12881.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_3', 3, NULL, 'assets/topic/asb/12871.png', 'I like playing with my friend Chuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_4', 3, NULL, 'assets/topic/asb/12872.png', 'We are good friends. When either one of us is sent somewhere, we go together.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_5', 3, NULL, 'assets/topic/asb/12873.png', 'One day, my grandmother sent me to the shop to buy salt and cooking oil. I went with Chuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_6', 3, NULL, 'assets/topic/asb/12874.png', 'On the way, we saw our friends playing football.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_7', 3, NULL, 'assets/topic/asb/12876.png', '', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_8', 3, NULL, 'assets/topic/asb/12877.png', 'I said to Chuma, "Let''s ask if we can play with them. I like playing football very much."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_9', 3, NULL, 'assets/topic/asb/12878.png', 'Chuma replied, "Let us go to the shop first, then we can come to play."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_10', 3, NULL, 'assets/topic/asb/12879.png', 'But I replied, "Let us play first! Grandmother will not allow us to return."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_11', 3, NULL, 'assets/topic/asb/12880.png', 'We joined our friends to play. I was the goal keeper. The goal posts were made of two big stones.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_12', 3, NULL, 'assets/topic/asb/12881.png', 'I worked hard to catch the ball. My friends were not able to score any goals.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_13', 3, NULL, 'assets/topic/asb/12882.png', 'We played until the field was full of sand.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_14', 3, NULL, 'assets/topic/asb/12883.png', 'Then we went to the shop. Grandmother''s money was gone! I started to cry. Chuma shouted, "Stop crying. You wanted to play first."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_15', 3, NULL, 'assets/topic/asb/12884.png', 'We returned home without salt or cooking oil. We were very dirty and worried.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_16', 3, NULL, 'assets/topic/asb/12885.png', 'Grandmother was annoyed when we got home. The first question she asked us was, "Where were you all this time?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_17', 3, NULL, 'assets/topic/asb/12886.png', 'We had to tell her the truth. She forgave us but warned, "Never again go to play without permission."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_18', 3, NULL, 'assets/topic/asb/12887.png', 'We went to feed the ducks.  Later on, we cleaned ourselves.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_19', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
