INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555', 2, 'Serious accident', 'assets/topic/asb/9638.png', 'Clare Verbeek
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_3', 3, NULL, 'assets/topic/asb/9635.png', 'One day my sister and I were walking near the road.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_4', 3, NULL, 'assets/topic/asb/9636.png', 'A big truck was driving down the hill.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_5', 3, NULL, 'assets/topic/asb/9637.png', 'A car was speeding up the hill.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_6', 3, NULL, 'assets/topic/asb/9641.png', 'The truck driver was talking on his mobile phone. He was not looking at the road.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_7', 3, NULL, 'assets/topic/asb/9639.png', 'The truck smashed into the car!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_8', 3, NULL, 'assets/topic/asb/9640.png', 'An ambulance and the police came to help.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_9', 3, NULL, 'assets/topic/asb/9638.png', 'Two tow trucks came. One towed the truck. The other towed the car.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10555_10', 3, NULL, 'assets/topic/asb/9642.png', 'We saw everything. We were shocked.', NULL);
