INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094', 2, 'Akadeli''s Lucky Day', 'assets/topic/asb/19016.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_3', 3, NULL, 'assets/topic/asb/19004.png', 'One day Akadeli, Lucy, Acharait and Mary went to the forest far from home to pick wild fruits. They held hands to cross the big river near the forest.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_4', 3, NULL, 'assets/topic/asb/19005.png', 'They found a tree full of ripe fruits. The girls agreed to climb up the tree and to pick the fruits with their eyes closed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_5', 3, NULL, 'assets/topic/asb/19007.png', 'Lucy, Acharait and Mary did not close their eyes. Only Akadeli closed hers as she picked the fruits.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_6', 3, NULL, 'assets/topic/asb/19009.png', 'When they opened their eyes, only Akadeli had picked unripe fruits.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_7', 3, NULL, 'assets/topic/asb/19010.png', 'Lucy, Acharait and Mary laughed at Akadeli and left for home. Akadeli threw away the unripe fruits and began picking ripe ones.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_8', 3, NULL, 'assets/topic/asb/19011.png', 'Soon, Akadeli filled her basket with ripe fruits. She set off alone,  crossing the big river.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_9', 3, NULL, 'assets/topic/asb/19012.png', 'Half way across the river, Akadeli''s basket fell and was carried away. She was angry and began to cry.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_10', 3, NULL, 'assets/topic/asb/19013.png', 'Before Akadeli got out of the water, she saw a big fish and grabbed it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_11', 3, NULL, 'assets/topic/asb/19014.png', 'As Akadeli carried the fish home, a hawk snatched it and flew away with it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_12', 3, NULL, 'assets/topic/asb/19015.png', 'Akadeli saw that the hawk had left behind a feather from its tail. Although she was disappointed, she picked up the feather and continued walking home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_13', 3, NULL, 'assets/topic/asb/19016.png', 'She came across a traditional wedding. People dancing were wearing grass on their heads instead of feathers as was the tradition. They took her feather and gave her a big bull!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_14', 3, NULL, 'assets/topic/asb/19017.png', 'Akadeli reached home with her bull. Her parents and relatives were so happy. Lucy, Acharait and Mary wished they had not lied to Akadeli. It was a lucky day for Akadeli!', NULL);
