INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862', 2, 'Happy Akai', 'assets/topic/asb/9500.png', 'Dominic E Emuria and David A Lodung''a
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_3', 3, NULL, 'assets/topic/asb/9494.png', 'Akai had no shoes. Other children her age     had shoes.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_4', 3, NULL, 'assets/topic/asb/9492.png', '"It is difficult to play without shoes," said Akai  to her mother.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_5', 3, NULL, 'assets/topic/asb/9493.png', '"Akai,  I think it is time for you to have shoes," said mother. .', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_6', 3, NULL, 'assets/topic/asb/9496.png', 'Mother woke up Akai   early the next morning.   .', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_7', 3, NULL, 'assets/topic/asb/9495.png', 'Akai and her mother went to the market. .', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_8', 3, NULL, 'assets/topic/asb/9497.png', 'They went into a shoe shop. Mother bought shoes for Akai.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_9', 3, NULL, 'assets/topic/asb/9499.png', 'At night,  Akai thought about her new shoes. .', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_10', 3, NULL, 'assets/topic/asb/10444.png', 'Akai put on her new shoes. .', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_11', 3, NULL, 'assets/topic/asb/9498.png', 'Akai thought about how she would show off her shoes. .', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_12', 3, NULL, 'assets/topic/asb/9502.png', 'Akai wore her new shoes again. She danced around the room.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_13', 3, NULL, 'assets/topic/asb/9501.png', '"Akai,  you must not wear shoes in bed," said mother. .', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_14', 3, NULL, 'assets/topic/asb/9500.png', 'Akai was so happy to have shoes for the first time.', NULL);
