INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806', 2, 'The one who loves me, knows what I love', 'assets/topic/asb/1196.png', 'Palmesi Katangambo
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_3', 3, NULL, 'assets/topic/asb/19837.png', 'Once upon a time, there was a king.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_4', 3, NULL, 'assets/topic/asb/17604.png', 'One day, the king called his wives and said, "My beloved wives, today I would like to have a delicious meal.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_5', 3, NULL, 'assets/topic/asb/9495.png', 'One of his wives went to the market and bought some beef and spices.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_6', 3, NULL, 'assets/topic/asb/14032.png', 'Another wife went to a different market. And bought the meat of wild animal with so many spices.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_7', 3, NULL, 'assets/topic/asb/10066.png', 'The last wife woke up and went to pay a visit to the king''s mother. She was told to prepare tradition dish without putting oil,or any modern spice.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_8', 3, NULL, 'assets/topic/asb/10069.png', 'The third wife went in the garden to look for different stuff in order to prepare traditional food.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_9', 3, NULL, 'assets/topic/asb/17605.png', 'The king together with his friends, reached to his wives who cooked meat. However, the king was not happy with what was prepared. Then they decided to go to the one who prepared traditional food.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22806_10', 3, NULL, 'assets/topic/asb/10074.png', 'The king was tremendously happy with that food, because he loved it. Finally he appointed her_ the third wife, to be the queen of his kingdom. From then, that woman became respectful in the entire neighborhood. It''s because she knew the ways of pleasing THE KING.', NULL);
