INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734', 2, 'Whoever doesn''t obey elders', 'assets/topic/asb/27127.png', 'Yakubu Aliyu Malumri
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_3', 3, NULL, 'assets/topic/asb/27118.png', 'Once upon a time, there was a boy called Jallo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_4', 3, NULL, 'assets/topic/asb/27119.png', 'One day Jallo and his mother went to visit their relatives in the village.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_5', 3, NULL, 'assets/topic/asb/27120.png', 'They took Jallo and his mother to the village''s fruit trees, and invited them to take all the fruit that they liked.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_6', 3, NULL, 'assets/topic/asb/27121.png', 'In the garden, Jallo saw a very big mango tree with many ripe mangoes.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_7', 3, NULL, 'assets/topic/asb/27122.png', 'Jallo was amazed to see so many ripe mangoes. He decided to pick one.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_8', 3, NULL, 'assets/topic/asb/27123.png', 'Although Jallo had been asked not to eat from the mango tree, he did not listen.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_9', 3, NULL, 'assets/topic/asb/27124.png', 'Jallo''s mother warned him again. But he refused to obey her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_10', 3, NULL, 'assets/topic/asb/27125.png', 'On eating the mango, his stomach suddenly started rumbling.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_11', 3, NULL, 'assets/topic/asb/27126.png', 'Jallo felt unwell. He started crying.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_12', 3, NULL, 'assets/topic/asb/27127.png', 'Then, a mango tree grew out of his head!
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_13', 3, NULL, 'assets/topic/asb/27128.png', 'Since that time, whenever someone touches the branches of that mango tree, it sings a song.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_14', 3, NULL, NULL, 'That''s how it is, and why wise people say, "Whoever refuses to listen to the elders, ends up regretting it."', NULL);
