INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830', 2, 'Bunty and Bubbly', 'assets/topic/asb/607.png', 'Sorit Gupto
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_3', 3, NULL, 'assets/topic/asb/607.png', 'Bunty loves to play with butterflies.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_4', 3, NULL, 'assets/topic/asb/612.png', 'And with birds.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_5', 3, NULL, 'assets/topic/asb/608.png', 'She loves to play with paper boats.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_6', 3, NULL, 'assets/topic/asb/610.png', 'She also likes to make sand castles.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_7', 3, NULL, 'assets/topic/asb/609.png', 'When Bunty goes back home,  her mother asks her to clean up. She refuses.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_8', 3, NULL, 'assets/topic/asb/611.png', '"I hate soap!" she screams.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_9', 3, NULL, 'assets/topic/asb/613.png', 'One night,  she has a dream. The germs are all around her castle,  and are attacking her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_10', 3, NULL, 'assets/topic/asb/614.png', 'Bunty is chased by the germs. She runs for her life and screams,  "Help. Help!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_11', 3, NULL, 'assets/topic/asb/911.png', 'Suddenly,  Soap King Bubbly appears. "Bunty,  don''t be afraid," he says. "Go attack the germs!" the Soap King orders his army of bubbles.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_12', 3, NULL, 'assets/topic/asb/910.png', 'The army of bubbles chases the germs away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2830_13', 3, NULL, 'assets/topic/asb/912.png', 'These days,  Bunty likes to use soap,  and she brushes and scrubs herself clean.', NULL);
