INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912', 2, 'Letters', 'assets/topic/asb/1398.png', 'Clare Verbeek
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_3', 3, NULL, 'assets/topic/asb/1398.png', 'Last week I received a lot of letters.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_4', 3, NULL, 'assets/topic/asb/1399.png', 'On Monday my doctor wrote a letter to my teacher.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_5', 3, NULL, 'assets/topic/asb/1400.png', 'Thabisile is sick. She must stay at home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_6', 3, NULL, 'assets/topic/asb/1401.png', 'On Tuesday I got a letter from my class.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_7', 3, NULL, 'assets/topic/asb/1402.png', 'Get well soon Thabisile!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_8', 3, NULL, 'assets/topic/asb/1403.png', 'On Wednesday I got a letter from my best friend.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_9', 3, NULL, 'assets/topic/asb/1404.png', 'I drew this picture for you. Love from Dudu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_10', 3, NULL, 'assets/topic/asb/1405.png', 'On Thursday I got a letter from my mother.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2912_11', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
