INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526', 2, 'Letter to mum', 'assets/topic/asb/1503.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_3', 3, NULL, 'assets/topic/asb/1504.png', 'Mother was finishing packing her clothes in a small bag.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_4', 3, NULL, 'assets/topic/asb/1505.png', 'As she went out the door,  she hugged me and said,  "Bye for now."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_5', 3, NULL, 'assets/topic/asb/1506.png', '"Where are you going,  mum?" I looked up at her as I asked.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_6', 3, NULL, 'assets/topic/asb/1507.png', 'I started to cry wanting to follow her but dad lifted me up on his shoulder.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_7', 3, NULL, 'assets/topic/asb/1508.png', 'Dad said,  "Your mother is going to the hospital to have a baby."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_8', 3, NULL, 'assets/topic/asb/2133.png', 'When I heard "baby" I stopped crying and looked at him. "Why is she going to get the baby from the hospital?" I asked him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_9', 3, NULL, 'assets/topic/asb/1509.png', 'Dad sat me on a small chair and said,  "I will tell you why she is going to get the baby from the hospital."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_10', 3, NULL, NULL, 'I listened keenly to him as he explained although I didn''t quite understand why she wasn''t having the baby at home. When dad finished explaining,  I asked,  "Will she still love me when she comes back with the other baby?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_11', 3, NULL, 'assets/topic/asb/1510.png', 'My dad held me close and said,  "We will always love you."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_12', 3, NULL, 'assets/topic/asb/1511.png', '"Can I write a letter to her while she is there?" I asked dad.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_13', 3, NULL, 'assets/topic/asb/1512.png', 'That evening,  I began to write a letter to mum.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_14', 3, NULL, 'assets/topic/asb/1513.png', 'I tore a paper from my exercise book and began to write.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_15', 3, NULL, 'assets/topic/asb/1514.png', '"Mummy,  mummy,  dad said that you have gone to bring another baby."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_16', 3, NULL, 'assets/topic/asb/1503.png', 'Please hurry up and return home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_17', 3, NULL, 'assets/topic/asb/1515.png', 'I want you to bring a baby girl,  so I can play with her when she grows up.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_18', 3, NULL, 'assets/topic/asb/1516.png', 'You can bring two so one can be mine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_19', 3, NULL, 'assets/topic/asb/1517.png', 'I am waiting for you here outside our house.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_20', 3, NULL, 'assets/topic/asb/1518.png', 'I folded my letter so I could give it to my father to bring it to mum. When I woke up the following morning,  the letter was gone.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2526_21', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
