INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('2526_31', 0, 1, 'Happy');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('2526_31', 0, 2, 'Excited');
