INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('2526_31', 0, 4, 'Sad');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428', 2, 'Big blue bus', 'assets/topic/asb/11446.png', 'Mecelin Kakoro
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_3', 3, NULL, 'assets/topic/asb/11446.png', 'There was only one bus in Ebei''s village. It was big and blue. It was very noisy.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_4', 3, NULL, 'assets/topic/asb/11447.png', '"Tomorrow we will go to town," said Ebei''s mother. "We will buy your school uniform."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_5', 3, NULL, 'assets/topic/asb/11448.png', 'Ebei was very excited. They would travel in the big blue bus. He could not sleep that night.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_6', 3, NULL, 'assets/topic/asb/11449.png', 'When his mother came to wake him, Ebei was already dressed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_7', 3, NULL, 'assets/topic/asb/11450.png', 'Ebei and his mother walked to the bus stop. They waited for the big blue bus. But the bus did not come.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_8', 3, NULL, 'assets/topic/asb/11451.png', 'Other people arrived at the bus stop. They complained because the bus was late. "Where is the bus?" they asked.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_9', 3, NULL, 'assets/topic/asb/11452.png', 'Ebei was worried. "We will not be able to go to town," he thought. "I will not be able to get my uniform."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_10', 3, NULL, 'assets/topic/asb/11453.png', 'Some people gave up and went home. Ebei cried. He did not want to go. "We will wait a bit longer," said his mother.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_11', 3, NULL, 'assets/topic/asb/11454.png', 'Suddenly, they heard a noise. They saw dust in the air. The bus was coming!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_12', 3, NULL, 'assets/topic/asb/11455.png', 'But this bus was not blue. It was not big. It was red and small. The people did not want to get in this bus.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_13', 3, NULL, 'assets/topic/asb/11456.png', '"Get in! Get in!" shouted the driver. "We are very late today," he called.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_14', 3, NULL, 'assets/topic/asb/11457.png', 'Ebei and his mother got in first. Soon everyone else got in the small red bus.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_15', 3, NULL, 'assets/topic/asb/11458.png', 'Ebei looked out the window. He saw more people at the bus stop.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_16', 3, NULL, 'assets/topic/asb/11459.png', 'Even more people were running to catch the bus. But they were too late. The red bus was full. It left for town.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_17', 3, NULL, 'assets/topic/asb/11460.png', '"Where is the big blue bus?" asked Ebei''s mother. "It broke down," replied the driver. "We are fixing it. It will come tomorrow."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_18', 3, NULL, 'assets/topic/asb/11461.png', 'Ebei did not care about the colour of the bus. He did not care about the size. This bus was going to town!', NULL);
