INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419', 2, 'Ms Phone', 'assets/topic/asb/10053.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_3', 3, NULL, 'assets/topic/asb/10048.png', 'She is called Ms Phone. Look at her!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_4', 3, NULL, 'assets/topic/asb/10049.png', 'She has five cellphones. When she wakes up,  she arranges her mobile phones on the table.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_5', 3, NULL, 'assets/topic/asb/10050.png', 'The yellow one is for communicating with family.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_6', 3, NULL, 'assets/topic/asb/10051.png', 'The white one is for communicating with people at work.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_7', 3, NULL, 'assets/topic/asb/10052.png', 'The red one is for communicating with those who owe her something.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_8', 3, NULL, 'assets/topic/asb/10053.png', 'The pink one is for communicating with her best friends.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_9', 3, NULL, 'assets/topic/asb/10054.png', 'The purple one is for communicating with people who need her help.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_10', 3, NULL, 'assets/topic/asb/10055.png', 'Ms Phone likes people to be happy. She does not like fighting.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_11', 3, NULL, 'assets/topic/asb/10056.png', 'She lets the children watch television at her shop.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_12', 3, NULL, 'assets/topic/asb/10057.png', 'She visits the sick and the elderly in their homes.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_13', 3, NULL, 'assets/topic/asb/10058.png', 'When she passes by, people shout out, "Ms Phone! Ms Phone!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_14', 3, NULL, 'assets/topic/asb/10059.png', 'Then she feels happy and says, "I am Ms Phone. Here,  look at my phones!" That is Ms Phone and her five mobile phones.', NULL);
