INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110', 2, 'Grandmother''s stories', 'assets/topic/asb/18842.png', 'Mutai Chepkoech
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_3', 3, NULL, 'assets/topic/asb/18819.png', 'We often gathered at grandmother''s house to listen to her stories. We liked to sit around the fire, under the stars on a cow skin. Grandmother began her last story for the night, "Hare and Elephant were neighbours and friends."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_4', 3, NULL, 'assets/topic/asb/18831.png', 'Hare liked mushrooms, but he was too lazy to farm. So he stole Elephant''s mushrooms. Elephant had planted bananas and pumpkins that year. So there were no mushrooms for Hare.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_5', 3, NULL, 'assets/topic/asb/18832.png', 'Hare went without food for many days. Then he decided to steal Elephant''s bananas. Elephant noticed that someone was stealing his bananas. He went to Hare and said, "Someone is stealing from me."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_6', 3, NULL, 'assets/topic/asb/18833.png', 'Hare asked, "Who is the thief?" Elephant began to sing,  "I will catch the thief! Aaah! I will catch the thief! Ooo!"  Hare felt nervous.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_7', 3, NULL, 'assets/topic/asb/18834.png', 'Elephant asked Monkey to watch his farm. But Hare did not steal that day or that night. Nor the next day or night. Monkey became tired of watching.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_8', 3, NULL, 'assets/topic/asb/18835.png', 'The following day, Hare went to Elephant''s farm to steal again. When Elephant came to check on his crops, Hare hid amongst the pumpkin leaves.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_9', 3, NULL, 'assets/topic/asb/18836.png', 'Elephant saw leaves move and began singing, "I''ve caught the thief! Aah! I''ve caught the thief! Ooo!" Elephant searched everywhere but did not find the thief.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_10', 3, NULL, 'assets/topic/asb/18837.png', 'On another day, Hare was there helping himself to Elephant''s crops. Elephant arrived at the farm and Hare jumped inside a huge pumpkin to hide.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_11', 3, NULL, 'assets/topic/asb/18839.png', 'Elephant noticed the large pumpkin and said, "Ooo! Here is a big pumpkin. I''ll eat that for lunch." And he swallowed the pumpkin whole!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_12', 3, NULL, 'assets/topic/asb/18840.png', 'Hare tried to jump out of the pumpkin, but could not. Elephant felt something jumping in his stomach and said, "What kind of pumpkin is this that jumps in my stomach?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_13', 3, NULL, 'assets/topic/asb/18841.png', 'Elephant spat out the pumpkin. Before he could examine it, Hare burst out of the pumpkin and ran away as fast as he could. Elephant never caught the thief.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_14', 3, NULL, 'assets/topic/asb/18842.png', 'Grandmother said, "And that is the end of the story." "Thank you grandmother," we sleepily chorused. Then we went to bed thinking about grandmother''s stories.', NULL);
