INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431', 2, 'Beloved daughter', 'assets/topic/asb/18758.png', 'Ritah Katetemera
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_3', 3, NULL, 'assets/topic/asb/18757.png', 'Once upon a time, there was a man and his wife. They had six boys and one girl.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_4', 3, NULL, 'assets/topic/asb/18758.png', 'The parents made a small pot as a gift for their daughter. Natabo loved that little pot. She loved to play with it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_5', 3, NULL, 'assets/topic/asb/18759.png', 'One day the boys broke Natabo''s pot. It broke into small pieces.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_6', 3, NULL, 'assets/topic/asb/18760.png', 'Natabo looked for her pot but did not find it. She got very upset, cried and refused to eat anything.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_7', 3, NULL, 'assets/topic/asb/18761.png', 'Natabo became even more upset. She ran away from her parents'' home and went to the forest. She climbed up the tallest tree.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_8', 3, NULL, 'assets/topic/asb/18762.png', 'The parents searched for their daughter. After some time,  they found her up the tallest tree in the forest.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_9', 3, NULL, 'assets/topic/asb/18763.png', 'Natabo''s brothers gathered together below the tree. They sang a song to get her to come down.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_10', 3, NULL, 'assets/topic/asb/18764.png', 'Natabo made fun of her brothers. She sang the same song, but imitating the boys. "Ninini-ninio, ninini-ninio. Ninini-ninio, ninini-ninio."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_11', 3, NULL, 'assets/topic/asb/18765.png', 'Natabo still refused to come down from the tree. So her friend came and tried to get Natabo to climb down.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_12', 3, NULL, 'assets/topic/asb/18766.png', 'When Natabo heard her friend, she gradually began to climb down. Her friend continued to sing until Natabo reached the ground.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_13', 3, NULL, 'assets/topic/asb/18767.png', 'Natabo and her friend were happy and hugged each other. They walked slowly back to Natabo''s home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20431_14', 3, NULL, 'assets/topic/asb/18768.png', 'When the girls reached home, Natabo''s brothers and parents were joyful. They prepared a party, and invited many friends and relatives.
