INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417', 2, 'Kalabushe the talkative', 'assets/topic/asb/9449.png', 'Gaspah Juma
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_3', 3, NULL, 'assets/topic/asb/9393.png', 'Long time ago,  there lived a girl whose name was Kalabushe. Kalabushe was a very talkative girl. Although her mother warned her not to talk too much,  Kalabushe did not listen.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_4', 3, NULL, 'assets/topic/asb/9392.png', 'Kalabushe''s aunt lived across the valley on the other side of Kalabushe''s village. One day she fell ill. She had nobody to take care of her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_5', 3, NULL, 'assets/topic/asb/9394.png', 'Kalabushe''s mother was very busy. It was late in the evening when she gave Kalabushe food to take to her sick aunt.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_6', 3, NULL, 'assets/topic/asb/9395.png', 'On the way,  Kalabushe met Sinson. Sinson was a hyena who had changed into a person.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_7', 3, NULL, 'assets/topic/asb/9448.png', 'Sinson asked Kalabushe,  "What are you carrying?" Kalabushe answered,  "I am carrying meat,  eggs and milk." Kalabushe''s mother had warned her not to say what she was carrying.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_8', 3, NULL, 'assets/topic/asb/9449.png', 'Kalabushe also said,  "I am taking the food to my sick aunt." Sinson licked his lips at the thought of the meat that Kalabushe was carrying.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_9', 3, NULL, 'assets/topic/asb/9450.png', 'Sinson quickly ran ahead of Kalabushe to her aunt''s house.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_10', 3, NULL, 'assets/topic/asb/9445.png', 'He swallowed Kalabushe''s aunt and then covered himself with her blanket.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_11', 3, NULL, 'assets/topic/asb/9447.png', 'When Kalabushe arrived,  the house was very quiet. She went inside and called out,  "Aunt,  where are you?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_12', 3, NULL, 'assets/topic/asb/9446.png', 'Kalabushe did not hear her aunt''s voice.  She went to the inner room where her aunt slept. She was surprised when she saw someone covered with a big blanket.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_13', 3, NULL, 'assets/topic/asb/9451.png', 'Kalabushe asked,  "Aunt,  why are your ears so big today?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_14', 3, NULL, 'assets/topic/asb/9452.png', 'Kalabushe asked again,  "Aunt, why are your eyes so big today?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_15', 3, NULL, 'assets/topic/asb/9453.png', 'Kalabushe finally asked,  "Aunt,  why is your mouth so big today?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_16', 3, NULL, 'assets/topic/asb/9522.png', 'Kalabushe kept talking even when she was inside Sinson''s stomach. She asked many questions.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_17', 3, NULL, 'assets/topic/asb/9523.png', 'In the end,  Sinson was so tired of Kalabushe''s many questions that he decided to spit her out.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18417_18', 3, NULL, 'assets/topic/asb/9455.png', 'Kalabushe and her aunt were rescued by the villagers.
