INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084', 2, 'Leopard and Antelope', 'assets/topic/asb/17309.png', 'Magabi Enyew Gessesse
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_3', 3, NULL, 'assets/topic/asb/17306.png', 'Leopard was always trying to catch Antelope.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_4', 3, NULL, 'assets/topic/asb/17307.png', 'And Antelope was always escaping from Leopard.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_5', 3, NULL, 'assets/topic/asb/17309.png', 'One day,  Leopard called out to Antelope,  "Let''s be friends. What you eat,  I don''t eat. Nothing makes us enemies."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_6', 3, NULL, 'assets/topic/asb/17310.png', 'Antelope agreed. So Leopard said,  "Let''s make an oath to be friends. If either of us breaks the oath,  her child will die."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_7', 3, NULL, 'assets/topic/asb/17311.png', 'They made an oath to be friends. At night,  Antelope slept under a tree. Leopard slept in the branches above.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_8', 3, NULL, 'assets/topic/asb/17312.png', 'Antelope soon became fat. Leopard became thin.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_9', 3, NULL, 'assets/topic/asb/17313.png', 'Leopard was tempted to eat fat Antelope. She said to herself,  "I don''t care about the oath. I don''t even have a child!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_10', 3, NULL, 'assets/topic/asb/17314.png', 'Leopard jumped down to catch Antelope. But she got caught between two branches.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_11', 3, NULL, 'assets/topic/asb/17315.png', 'Antelope was shocked. She leaped up and cried,  "Beh! Beh!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_12', 3, NULL, 'assets/topic/asb/17316.png', 'Leopard begged,  "My friend,  help me. We agreed that whoever broke our oath to be friends,  would lose a child."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_13', 3, NULL, 'assets/topic/asb/17317.png', 'Antelope replied,  "It must have been your parent who made the oath. Now it is meant for you!"', NULL);
