INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434', 2, 'Frog and Snake', 'assets/topic/asb/18784.png', 'John Emongot
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_3', 3, NULL, 'assets/topic/asb/18783.png', 'Once upon a time, Frog and Snake were good friends. Their homes were near each other and their children played together.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_4', 3, NULL, 'assets/topic/asb/18784.png', 'One day,  Frog said to Snake, "My friend I want to visit my mother next week. Would you like to go with me?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_5', 3, NULL, 'assets/topic/asb/25000.png', '"I would be happy to go," Snake answered.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_6', 3, NULL, 'assets/topic/asb/25001.png', 'They asked Monkey to help since he was good and responsible. Monkey accepted.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_7', 3, NULL, 'assets/topic/asb/25002.png', 'During the next few days, the two friends were busy making arrangements for the journey.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_8', 3, NULL, 'assets/topic/asb/25003.png', 'The day arrived and the two friends set off at dawn.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_9', 3, NULL, 'assets/topic/asb/25004.png', 'Snake was so afraid that she started shaking. She hissed and hissed but the fierce squirrels kept on coming closer.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_10', 3, NULL, 'assets/topic/asb/25005.png', 'Just as the squirrels were about to attack them, Frog croaked very loudly.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_11', 3, NULL, 'assets/topic/asb/25006.png', 'When the two friends finally arrived at Frog''s mother''s place, they were hungry and tired.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_12', 3, NULL, 'assets/topic/asb/25007.png', 'When the food was ready, Frog and Snake washed and sat down to eat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_13', 3, NULL, 'assets/topic/asb/25008.png', 'Before they started eating, Frog said, "Snake you can''t eat while lying down. That is bad manners. Please sit up properly otherwise my mother will be offended."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_14', 3, NULL, 'assets/topic/asb/25009.png', 'Snake tried very hard to sit up like the frogs, but she failed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_15', 3, NULL, 'assets/topic/asb/25010.png', 'Meanwhile Frog and her mother started eating the food.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_16', 3, NULL, 'assets/topic/asb/25011.png', '"I can''t sit up properly," Snake pleaded with Frog. But Frog would not listen.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_17', 3, NULL, 'assets/topic/asb/25012.png', 'Snake was so upset that she decided to go back to her home. She did not say farewell to the frogs.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_18', 3, NULL, 'assets/topic/asb/25013.png', 'On the way home, she made a plan to teach Frog a lesson!', NULL);
