INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086', 2, 'Zama is great!', 'assets/topic/asb/7514.png', 'Nina Orange
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_3', 3, NULL, 'assets/topic/asb/7513.png', 'My little brother sleeps late.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_4', 3, NULL, 'assets/topic/asb/7514.png', 'I am the one who lets in the sun.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_5', 3, NULL, 'assets/topic/asb/7517.png', '"You''re my morning star," says Ma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_6', 3, NULL, 'assets/topic/asb/7507.png', 'I wash myself every day,  I don''t need any help.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_7', 3, NULL, 'assets/topic/asb/7515.png', 'I don''t mind cold water, or the blue soap that washes clothes.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_8', 3, NULL, 'assets/topic/asb/7518.png', 'Ma reminds,  "Don''t forget your teeth."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_9', 3, NULL, 'assets/topic/asb/7516.png', 'After washing,  I greet Grandpa and Auntie.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_10', 3, NULL, 'assets/topic/asb/7511.png', 'Then I dress myself.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_11', 3, NULL, 'assets/topic/asb/7508.png', 'I can close my buttons and buckle my shoes.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_12', 3, NULL, 'assets/topic/asb/7512.png', 'I make sure my brother knows all the school news.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_13', 3, NULL, 'assets/topic/asb/7510.png', 'In class I do my best in every way.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_14', 3, NULL, 'assets/topic/asb/7509.png', 'I do all these good things every day.
