INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445', 2, 'Pretty golden pen', 'assets/topic/asb/25333.png', 'Ahmed Mardiya and Ibrahim Sharifa
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_3', 3, NULL, 'assets/topic/asb/25336.png', 'Long ago, in Pen Village, lived many pens.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_4', 3, NULL, 'assets/topic/asb/25337.png', 'There was Golden, Bic, Ballpoint, Gel, Marker, and others.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_5', 3, NULL, 'assets/topic/asb/25339.png', 'Golden was a pretty girl.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_6', 3, NULL, 'assets/topic/asb/25340.png', 'Bic was Golden’s best friend.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_7', 3, NULL, 'assets/topic/asb/25341.png', 'But Golden was always sad and worried. She chose a lonely life away from all who loved her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_8', 3, NULL, 'assets/topic/asb/25342.png', 'Nobody in Pen Village knew why Golden was always sad and worried. They believed she had all the reasons to be happy.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_9', 3, NULL, 'assets/topic/asb/25343.png', 'However, Golden had a big secret only she knew about.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21445_10', 3, NULL, 'assets/topic/asb/25333.png', 'Although Golden was pretty and adored, she had no ink!
