INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807', 2, 'Peace', 'assets/topic/asb/8945.png', 'Niyifasha Epimaque
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_3', 3, NULL, 'assets/topic/asb/7987.png', 'Peace is very important thing in our daily life.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_4', 3, NULL, 'assets/topic/asb/8999.png', 'whoever does not have peace does not plan or do anything right.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_5', 3, NULL, 'assets/topic/asb/13838.png', 'If you don''t have peace, you lose the living hope and you feel that whatever you do yields nothing.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_6', 3, NULL, 'assets/topic/asb/18929.png', 'Peace is the root of living hope, wealthy and sustainable development.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_7', 3, NULL, 'assets/topic/asb/6006.png', 'A family with peace is so strong that nothing can demolish or divide its members.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_8', 3, NULL, 'assets/topic/asb/8479.png', 'Friends with peace love each other a true love and they live without any disruption.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_9', 3, NULL, 'assets/topic/asb/13388.png', 'A country with peace does not have wars and what the citizens will, they gather their force and earn it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30807_10', 3, NULL, 'assets/topic/asb/10611.png', 'May peace be with you all!', NULL);
