INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_4', 3, NULL, 'assets/topic/asb/15492.png', 'He had a donkey too.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_5', 3, NULL, 'assets/topic/asb/15493.png', 'In the evening, the ox told the donkey, "I am very tired today."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_6', 3, NULL, 'assets/topic/asb/15494.png', 'The ox told the donkey, "I did not rest today."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_7', 3, NULL, 'assets/topic/asb/15495.png', 'The donkey replied, "I worked harder than you."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_8', 3, NULL, 'assets/topic/asb/15496.png', 'The next day, the ox said, "Today was a bad day."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_9', 3, NULL, 'assets/topic/asb/15497.png', 'The donkey said, "When the farmer comes, say, ''Moo!'' He will let you rest."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_10', 3, NULL, 'assets/topic/asb/15498.png', 'The next morning, the ox cried, ''Moo!''
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_11', 3, NULL, 'assets/topic/asb/15499.png', 'The farmer took the donkey out to plough the land.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_12', 3, NULL, 'assets/topic/asb/15500.png', 'The donkey worked hard all day.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_13', 3, NULL, 'assets/topic/asb/15501.png', 'The donkey said, "The farmer said he will kill you for meat."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_14', 3, NULL, 'assets/topic/asb/15502.png', 'The ox said, "I will work tomorrow. I feel better now."', NULL);
