INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('15612_27', 0, 1, 'Makes us happy');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('15612_27', 0, 2, 'Livelihood');
