INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998', 2, 'Fana and her animals', 'assets/topic/asb/25266.png', 'Foziya Mohammed
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_3', 3, NULL, 'assets/topic/asb/25265.png', 'Fana and her family live in Debre Birhan, a busy town in Ethiopia. Fana is a Grade 3 student. She is good-hearted and wise.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_4', 3, NULL, 'assets/topic/asb/25266.png', 'Fana loves animals. She has a cat, two hens, a goat and a dove. She spends time with these animals, feeding them, and playing with them.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_5', 3, NULL, 'assets/topic/asb/25267.png', 'One day, Fana was playing in the school yard with her friends when she saw children throwing stones at doves.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_6', 3, NULL, 'assets/topic/asb/25268.png', 'She stopped playing and ran towards the children throwing stones.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_7', 3, NULL, 'assets/topic/asb/25269.png', 'The other children ran away.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_8', 3, NULL, 'assets/topic/asb/25270.png', 'She fed the doves. In the evening she told her family what she saw and did during the day, and how she saved the doves.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_9', 3, NULL, 'assets/topic/asb/25271.png', 'In the morning Fana and her parents went to the clinic and got medicine for the doves.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21998_10', 3, NULL, 'assets/topic/asb/25272.png', 'Fana always tells her friends, "I love animals. Animals are useful to us and they are our friends. We must protect and preserve them."', NULL);
