INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('15613_29', 0, 1, 'Kindness');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('15613_29', 0, 2, 'Happiness');
