INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199', 2, 'Happy and Sad', 'assets/topic/asb/1901.png', 'Madhav Chavan
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_3', 3, NULL, 'assets/topic/asb/1892.png', 'Why is the baby crying?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_4', 3, NULL, 'assets/topic/asb/1893.png', 'I think she is hungry.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_5', 3, NULL, 'assets/topic/asb/1894.png', 'Why is the boy sad?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_6', 3, NULL, 'assets/topic/asb/1895.png', 'I think he is missing his father.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_7', 3, NULL, 'assets/topic/asb/1896.png', 'Why is the girl happy?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_8', 3, NULL, 'assets/topic/asb/1897.png', 'She has a new dress.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_9', 3, NULL, 'assets/topic/asb/1898.png', 'Why is the old man laughing?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_10', 3, NULL, 'assets/topic/asb/1899.png', 'He read a good joke.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_11', 3, NULL, 'assets/topic/asb/1900.png', 'Why is the lady smiling?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_12', 3, NULL, 'assets/topic/asb/1901.png', 'Her children are reading quietly.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_13', 3, NULL, 'assets/topic/asb/1902.png', 'What is this lady feeling?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16199_14', 3, NULL, 'assets/topic/asb/1903.png', 'I think she is feeling shy.', NULL);
