INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040', 2, 'Hamisi''s lucky day', 'assets/topic/asb/7976.png', 'Adelheid Marie Bwire
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_3', 3, NULL, 'assets/topic/asb/7966.png', 'Hamisi needs a new school uniform. His dad takes him to the market.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_4', 3, NULL, 'assets/topic/asb/7967.png', 'Hamisi sees a boy wearing a beautiful shirt. "That is a very fine shirt," thinks Hamisi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_5', 3, NULL, 'assets/topic/asb/7968.png', '"Dad, I wish I had a shirt like the one that boy has," says Hamisi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_6', 3, NULL, 'assets/topic/asb/7969.png', '"Look, there are those beautiful shirts," says Hamisi. "Dad, please buy one for me."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_7', 3, NULL, 'assets/topic/asb/7972.png', '"Hamisi, I have to buy your uniform," says dad.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_8', 3, NULL, 'assets/topic/asb/7973.png', '"I do not have enough money for that shirt," says dad.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_9', 3, NULL, 'assets/topic/asb/7975.png', 'Hamisi''s dad buys the uniform. He is surprised when he gets the change. He has enough money left over to buy a shirt!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_10', 3, NULL, 'assets/topic/asb/7976.png', 'It is Hamisi''s lucky day! Which shirt will he choose?', NULL);
