INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838', 2, 'Fox and Rooster', 'assets/topic/asb/14805.png', 'Vincent Afeku
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_3', 3, NULL, 'assets/topic/asb/14795.png', 'There was a time when all the animals lived together as friends. One day they decided to have a meeting to elect a king.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_4', 3, NULL, 'assets/topic/asb/14796.png', 'Rooster wanted to be king. So he lied to the other animals. He told them,  "The red comb on my head is fire! Don''t touch the comb on my head!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_5', 3, NULL, 'assets/topic/asb/14797.png', 'The other animals believed Rooster. They decided to elect Rooster as their king. The animals hoped that Rooster''s fire would help them in cold weather.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_6', 3, NULL, 'assets/topic/asb/14798.png', 'Then came a day when it rained. And rained. And rained. It was cold. Everything was wet and cold.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_7', 3, NULL, 'assets/topic/asb/14799.png', '"Where can we get fire to warm our bodies?" Rabbit asked the other animals.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_8', 3, NULL, 'assets/topic/asb/14800.png', '"We can get fire from the comb on Rooster''s head," replied Monkey. "Rooster is our king because he has fire on his head," said Monkey.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_9', 3, NULL, 'assets/topic/asb/14801.png', 'The animals agreed with Monkey. They sent Fox to collect fire from king Rooster.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_10', 3, NULL, 'assets/topic/asb/14802.png', 'Fox found Rooster in a deep sleep. So he decided to take fire without asking Rooster.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_11', 3, NULL, 'assets/topic/asb/14803.png', 'Fox collected some dry grass. He put the grass next to the comb on Rooster''s head. Nothing happened. The grass did not catch fire.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_12', 3, NULL, 'assets/topic/asb/14804.png', '"Wake up king Rooster!" yelled Fox. "We need fire now."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_13', 3, NULL, 'assets/topic/asb/14805.png', 'But Rooster could not give fire to Fox.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21838_14', 3, NULL, 'assets/topic/asb/14806.png', 'Fox was angry. He went back to the other animals. He told them the truth about the comb on Rooster''s head. From that day,  no one was afraid of Rooster.  And foxes eat roosters and their families.', NULL);
