INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609', 2, 'Rooster and Hare', 'assets/topic/asb/12453.png', 'Geoffrey Thiiru
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_3', 3, NULL, 'assets/topic/asb/12438.png', 'Once upon a time,  there was a rooster and a hare. They were good friends.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_4', 3, NULL, 'assets/topic/asb/12440.png', 'One night,  Hare visited Rooster and his family. They enjoyed supper together.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_5', 3, NULL, 'assets/topic/asb/12443.png', 'When it was bedtime,  Rooster and his family soon fell asleep. Hare could not see their heads.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_6', 3, NULL, 'assets/topic/asb/12449.png', 'Rooster and his family slept very well. Hare was not able to sleep the whole night.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_7', 3, NULL, 'assets/topic/asb/12453.png', 'In the morning,  Hare wanted to know Rooster''s secret. "Where do you put your heads at night?" he asked.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_8', 3, NULL, 'assets/topic/asb/12454.png', 'Rooster decided to play a joke on Hare. He said,  "At bedtime,  we chop off our heads. Without heads,  we sleep very well."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_9', 3, NULL, 'assets/topic/asb/12455.png', '"Then in the morning,  we put our heads back on," explained Rooster.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_10', 3, NULL, 'assets/topic/asb/12456.png', 'Hare went home and told his family about the rooster''s secret. The Hare family decided to try it themselves. What do you think happened?', NULL);
