INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439', 2, 'Mod the toad', 'assets/topic/asb/13417.png', 'Alice Mulwa
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_3', 3, NULL, 'assets/topic/asb/13415.png', 'It was a cold and wet morning in the forest. Mod the little toad was tired of swimming in cold water. "I need to leave this place during the rainy season," she said to herself.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_4', 3, NULL, 'assets/topic/asb/13416.png', '"I want an adventure," said Mod to Tortoise. "I must find out about the world." "Be careful,  don''t leave the water for too long or your skin will dry out," warned the wise terrapin. At that time,  toads still had smooth wet skin like frogs. Without water,  their skins would become too dry.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_5', 3, NULL, 'assets/topic/asb/13417.png', 'But Mod the toad was curious. "I must find out, I must go beyond the forest," she said. Mod hopped out of the water. Hop, hop, she went, away from the river. She had fun jumping over the rocks, across grass, and past trenches.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_6', 3, NULL, 'assets/topic/asb/13418.png', 'She heard the sound of an air plane. Mod looked up into the sky. "What is up there?" she asked, "I wonder if the clouds are talking." But no one answered her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_7', 3, NULL, 'assets/topic/asb/13419.png', 'Mod carried on her way. She met Chameleon for the first time. He was next to a large puddle of water. "Hello, I am on my way to dry land for adventure. What can you tell me?" she asked. "Nothing much stranger. It rains here too. Lots of rain and lots of flies," replied Chameleon.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_8', 3, NULL, 'assets/topic/asb/13421.png', 'As Chameleon was talking, his body changed colour. Mod got a fright. She quickly said goodbye to Chameleon and hopped into a nearby bush.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_9', 3, NULL, 'assets/topic/asb/13422.png', 'But she was unlucky. Hyena was busy burning bushes in preparation for planting season.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_10', 3, NULL, 'assets/topic/asb/13423.png', 'Mod did not want to turn around for fear of meeting Chameleon again. She stayed in the bush, only her round eyes could be seen. They were big with fear. Mod was surrounded by fire!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_11', 3, NULL, 'assets/topic/asb/13424.png', 'Mod did not know where to go. The flames began to burn her back. Then the little toad remembered the puddle of water where she met Chameleon. She closed her eyes, held her breath, and jumped high in the direction of the puddle.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_12', 3, NULL, 'assets/topic/asb/13425.png', 'She was lucky this time. She landed in the puddle,  and the water put out the flames that were burning her skin. But the damage was done. When she came out of the puddle, the skin on her back was full of blisters.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_13', 3, NULL, 'assets/topic/asb/13426.png', 'Mod did not want an adventure anymore. She wanted to go home. But she did not know the way back to her river in the forest. So the little toad found a cool, dark place under a rock and sadly made a new home. Her burned skin slowly healed, but it was dry and bumpy.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20439_14', 3, NULL, 'assets/topic/asb/13427.png', 'This is why toads have dry rough skin, and can live away from water. Frogs still have smooth wet skin and need to stay where it''s wet.', NULL);
