INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557', 2, 'How colours came together in the sky', 'assets/topic/asb/26440.png', 'Zainab Ayoza Omaki
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_3', 3, NULL, 'assets/topic/asb/26434.png', 'One day after a heavy rain, Iman looked across the sky and saw colours in it for the first time.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_4', 3, NULL, 'assets/topic/asb/26435.png', 'How beautiful, she thought. Someone must have worked very hard, painting the sky with a brush like Daddy paints houses.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_5', 3, NULL, 'assets/topic/asb/26436.png', 'She ran to her sister and said, "Zarah look! Someone has painted the sky for us and made it beautiful."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_6', 3, NULL, 'assets/topic/asb/26437.png', 'But Zarah said, "No, silly. That is a rainbow. You are little and I am big. I know why it is in the sky. Let me show you."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_7', 3, NULL, 'assets/topic/asb/26438.png', 'Zarah took Iman to the zoo. She said, "Rainbows do not appear because lion cubs roar for the first time or because elephants are born."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_8', 3, NULL, 'assets/topic/asb/26439.png', 'She took Iman to a field of flowers and said, "They do not appear because butterflies lose their colours in the clouds."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_9', 3, NULL, 'assets/topic/asb/26440.png', 'She showed Iman a book. She said, "They do not even appear because rhinos run across the ground and scare colours up into the sky."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_10', 3, NULL, 'assets/topic/asb/26441.png', '"I know why rainbows really appear. Mummy told me and now I''ll tell you."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_11', 3, NULL, 'assets/topic/asb/26442.png', '"Rainbows appear in the sky because of children like you and me. One day, colours looked down from above and liked what they could see."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_12', 3, NULL, 'assets/topic/asb/26443.png', '"They saw children with light skin, dark skin and everything in between. They thought, ''How wonderful it must be to be as beautiful as these.''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_13', 3, NULL, 'assets/topic/asb/26444.png', '"They came together in the sky and spoke about what they could do. They decided, ''If we are to be beautiful we must do it as a group.''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_14', 3, NULL, 'assets/topic/asb/26445.png', '"Now they join together and shine as brightly as they can. Hoping to be as beautiful as the children they see, just like you and me."', NULL);
