INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081', 2, 'Sun, Moon, and Water', 'assets/topic/asb/25567.png', 'Athieno Gertrude
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_3', 3, NULL, 'assets/topic/asb/25557.png', 'Many years ago, Sun and Water were great friends. Both lived on earth together.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_4', 3, NULL, 'assets/topic/asb/25558.png', 'Sun used to visit Water very often, but Water never went to visit Sun.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_5', 3, NULL, 'assets/topic/asb/25559.png', 'At last Sun asked Water why he never came to visit.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_6', 3, NULL, 'assets/topic/asb/25560.png', 'Then Water said, "If you wish me to visit you, build a very large compound. My people are very many, and take up a lot of room."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_7', 3, NULL, 'assets/topic/asb/25561.png', 'Sun returned home to his wife, Moon, who greeted him with a broad smile. Sun told Moon what he had promised Water.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_8', 3, NULL, 'assets/topic/asb/25562.png', 'The next day he started building a huge compound in which to entertain his friend.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_9', 3, NULL, 'assets/topic/asb/25563.png', 'As soon as the compound was completed, he asked Water to come and visit them.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_10', 3, NULL, 'assets/topic/asb/25564.png', 'When Water arrived, he asked Sun whether it would be safe for him to enter.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_11', 3, NULL, 'assets/topic/asb/25565.png', 'Water began to flow in.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_12', 3, NULL, 'assets/topic/asb/25566.png', 'Very soon Water was knee-deep. He asked Sun if it was still safe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_13', 3, NULL, 'assets/topic/asb/25567.png', 'Water reached the level of the top of a person''s head. He asked Sun, "Can more of my people come in?" Sun and Moon both answered, "Yes."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_14', 3, NULL, 'assets/topic/asb/25568.png', 'Water''s people continued to rush in. Very soon they reached the top of the roof.
