INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468', 2, 'Kate is in heaven', 'assets/topic/asb/26149.png', 'Cornelius Wambi Gulere
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_3', 3, NULL, 'assets/topic/asb/26134.png', 'Kate has a thing in her head.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_4', 3, NULL, 'assets/topic/asb/26135.png', 'It makes her tired.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_5', 3, NULL, 'assets/topic/asb/26136.png', 'She can''t eat. She can''t drink.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_6', 3, NULL, NULL, 'Oh dear. Poor woman! Her head throbs with pain.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_7', 3, NULL, 'assets/topic/asb/26137.png', 'The doctor sees a swelling in her head and says, "There is a tumour in your brain."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_8', 3, NULL, 'assets/topic/asb/26138.png', '"We have to remove it from your brain right now. We have to remove it now!" the doctor says.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_9', 3, NULL, 'assets/topic/asb/26139.png', 'They bring a stretcher.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_10', 3, NULL, 'assets/topic/asb/26140.png', 'They arrange the surgical blades.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_11', 3, NULL, 'assets/topic/asb/26141.png', 'They inject her with syringes.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_12', 3, NULL, 'assets/topic/asb/26142.png', 'They cut open Kate''s head.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_13', 3, NULL, 'assets/topic/asb/26143.png', 'They remove the brain.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_14', 3, NULL, 'assets/topic/asb/26144.png', 'They cut out the tumour.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_15', 3, NULL, 'assets/topic/asb/26145.png', 'They return the brain to the skull.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_16', 3, NULL, 'assets/topic/asb/26146.png', 'Does Kate feel everything they do?
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_17', 3, NULL, 'assets/topic/asb/26147.png', 'Then she comes back to life!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_18', 3, NULL, 'assets/topic/asb/26148.png', 'Now she is well. Like a new born baby.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_19', 3, NULL, 'assets/topic/asb/26149.png', 'We are all happy for our friend Kate, who tasted heaven.', NULL);
