INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544', 2, 'Khalai talks to plants', 'assets/topic/asb/13389.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_3', 3, NULL, 'assets/topic/asb/13382.png', 'This is Khalai. She is seven years old. Her name means ''the good one.''', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_4', 3, NULL, 'assets/topic/asb/13383.png', 'Khalai wakes up and talks to the orange tree. "Please orange tree, grow big and give us lots of ripe oranges."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_5', 3, NULL, 'assets/topic/asb/13384.png', 'Khalai walks to school. On the way,  she talks to the grass. "Please grass,  grow greener and don''t dry up."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_6', 3, NULL, 'assets/topic/asb/13385.png', 'Khalai sees flowers. "Please flowers,  keep blooming so I can put you in my hair."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_7', 3, NULL, 'assets/topic/asb/13386.png', 'Khalai talks to the tree at her school. "Please tree,  put out big branches so we can read under your shade."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_8', 3, NULL, 'assets/topic/asb/13387.png', 'Khalai talks to the hedge round her school. "Please,  grow strong and protect us from bad people."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_9', 3, NULL, 'assets/topic/asb/13388.png', 'When Khalai returns home,  she visits the orange tree. "Are your oranges ripe yet?" Khalai asks the tree.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_10', 3, NULL, 'assets/topic/asb/13389.png', '"The oranges are still green," sighs Khalai.  "I will see you tomorrow orange tree," says Khalai. "Perhaps then,  you will have a ripe orange for me!"', NULL);
