INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567', 2, 'Drought and the River of Blessings', 'assets/topic/asb/26514.png', 'Fary  Silate and Mohammed Sale Also
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_3', 3, NULL, 'assets/topic/asb/26503.png', 'In a particular year, the dry season was too long and a drought hit hard. As a result, all the rivers dried up, except the River of Blessings.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_4', 3, NULL, 'assets/topic/asb/26504.png', 'Camel was the first to speak, "As you know, we are very thirsty and all the rivers have dried up, except the River of Blessings. But it is very far away. What do we do?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_5', 3, NULL, 'assets/topic/asb/26505.png', 'Horse suggested, "Some of us can go to the River of Blessings. We can drink and bring water for the others."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_6', 3, NULL, 'assets/topic/asb/26506.png', 'Sheep said, "Maa! Maaa! I will go to the River of Blessings and drink water."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_7', 3, NULL, 'assets/topic/asb/26507.png', 'All the animals wanted to go. Camel led the way.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_8', 3, NULL, 'assets/topic/asb/26508.png', 'They continued. Chicken said, "Please, I am tired, I no longer want to go to the River of Blessings."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_9', 3, NULL, 'assets/topic/asb/26509.png', 'As the journey continued, Sheep also became tired.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_10', 3, NULL, 'assets/topic/asb/26510.png', 'Soon, Goat was too tired to continue going to the river. Horse turned and swallowed Goat.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_11', 3, NULL, 'assets/topic/asb/26511.png', 'When Cow finally could not continue, Camel turned and swallowed her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_12', 3, NULL, 'assets/topic/asb/26512.png', 'Being the only animal left, Camel did her best to reach the River of Blessings.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_13', 3, NULL, 'assets/topic/asb/26513.png', 'On arrival, Camel lay down and vomited Cow.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_14', 3, NULL, 'assets/topic/asb/26514.png', 'And that is how all the animals managed to drink water from the River of Blessings.', NULL);
