INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440', 2, 'Monkey and the Drought', 'assets/topic/asb/18796.png', 'Alice Edui
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_3', 3, NULL, 'assets/topic/asb/18786.png', 'There was severe drought across the land. Bushes and trees dried up. Rivers stopped flowing. There was no water and no food. People,  cattle and wild animals grew thin.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_4', 3, NULL, 'assets/topic/asb/18788.png', 'Many of the monkeys who lived in the hills died of hunger. One day,  a female monkey decided to look for a place where there was no drought.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_5', 3, NULL, 'assets/topic/asb/18789.png', 'Monkey travelled for many days. She passed bushes,  climbed hills,  and crossed valleys.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_6', 3, NULL, 'assets/topic/asb/18790.png', 'Finally,  Monkey arrived at a place called Tirkol.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_7', 3, NULL, 'assets/topic/asb/18791.png', 'At Tirkol,  Monkey found different kinds of fruit and a flowing river. She was very happy.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_8', 3, NULL, 'assets/topic/asb/18792.png', 'Monkey stayed there. She ate plenty of fruit. She grew fat and shiny. She swam in the river every day.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_9', 3, NULL, 'assets/topic/asb/18793.png', 'After some time,  Monkey decided to return to the hills. She wanted to see who had survived the drought. She took fruit and followed the path she had used before.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_10', 3, NULL, 'assets/topic/asb/18794.png', 'When she reached the hills,  the surviving monkeys came to welcome her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_11', 3, NULL, 'assets/topic/asb/18795.png', 'Everyone wanted the fruit she brought. They asked her,  "Where is this wonderful place? We want to go there too."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_12', 3, NULL, 'assets/topic/asb/18796.png', 'Monkey told them about the goodness of Tirkol and agreed to take them there.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_13', 3, NULL, 'assets/topic/asb/18797.png', 'All the monkeys moved to Tirkol. They ate fruit,  drank water,  rested and grew fat. They swore never to return to the hills.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_14', 3, NULL, 'assets/topic/asb/18798.png', 'The monkeys who lived in Tirkol soon discovered that monkeys from the hills had moved to their territory.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_15', 3, NULL, 'assets/topic/asb/18799.png', 'The Tirkol monkeys worried that the fruit would disappear. So they decided to chase away the monkeys from the hills.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_16', 3, NULL, 'assets/topic/asb/18800.png', 'One day,  the monkeys from the hills were at the river drinking water. The Tirkol monkeys attacked them with canes. They wanted to chase them away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_17', 3, NULL, 'assets/topic/asb/18801.png', 'But just before they started fighting,  an elderly monkey hung down from a branch. He asked the monkeys,  "Why do you want to fight? There is enough food for all of us."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20440_18', 3, NULL, 'assets/topic/asb/18802.png', 'All the monkeys realised that this was true. And so,  since that day,  monkeys from the hills and monkeys from Tirkol have lived together without fighting.', NULL);
