INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('20440_29', 0, 4, 'Stay far away from other monkeys.');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140', 2, 'Rabbit under the tree', 'assets/topic/asb/7610.png', 'Phumy Zikode
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_3', 3, NULL, 'assets/topic/asb/7595.png', 'Rabbit was sleeping under an apple tree. An apple fell down from a branch.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_4', 3, NULL, 'assets/topic/asb/7596.png', 'A voice said,  "Run Rabbit run!" She woke up quickly and ran away with great speed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_5', 3, NULL, 'assets/topic/asb/7597.png', 'She met Chicken. "Why are you running?" asked Chicken. Rabbit replied, "I don''t know. I just heard something falling and a voice said,  ''Run Rabbit run!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_6', 3, NULL, 'assets/topic/asb/7598.png', 'Chicken was frightened. She started running when she heard what Rabbit said.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_7', 3, NULL, 'assets/topic/asb/7599.png', 'They met Dog. Dog asked,  "Why are you running?" Chicken said,  "I don''t know. I just heard what Rabbit said, and she doesn''t know. She heard something falling and a voice said,  ''Run Rabbit run!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_8', 3, NULL, 'assets/topic/asb/7600.png', 'Dog was surprised about what she heard. She started running with Rabbit and Chicken.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_9', 3, NULL, 'assets/topic/asb/7601.png', 'They met Horse. Horse asked Dog, "Why are you running?" Dog said, "I don''t know. I just heard what Chicken said, and she doesn''t know. She heard what Rabbit said and she doesn''t know. She heard something falling and a voice said, ''Run Rabbit run!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_10', 3, NULL, 'assets/topic/asb/7602.png', 'Horse started to run with the others.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_11', 3, NULL, 'assets/topic/asb/7603.png', 'They met Donkey. Donkey asked, "Why are you running?" Horse replied, "I don''t know. I just heard what Dog said, and he doesn''t know. He heard what Chicken said and she doesn''t know. She heard what Rabbit said and she doesn''t know. She heard something falling and a voice said, ''Run Rabbit run!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_12', 3, NULL, 'assets/topic/asb/7604.png', 'So Donkey starts running too.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_13', 3, NULL, 'assets/topic/asb/7605.png', 'They met Cow. Cow asked, "Why are you running?" Donkey replied, "I don''t know. I just heard what Horse said, and she doesn''t know. She heard what Dog said and he doesn''t know. He heard what Chicken said and she doesn''t know. She heard what Rabbit said and she doesn''t know. She heard something falling and a voice said, ''Run rabbit run!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_14', 3, NULL, 'assets/topic/asb/7606.png', 'Cow was worried and starte to run as well.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_15', 3, NULL, 'assets/topic/asb/7607.png', 'They met Cat. Cat asked, "Why are you running?" Cow replied, "I don''t know. I just heard what Donkey said,  and he doesn''t know. He heard what Horse said and she doesn''t know. She heard what Dog said and he doesn''t know. He heard what Chicken said and she doesn''t know. She heard what Rabbit said and she doesn''t know. She heard something falling and a voice said, ''Run rabbit run!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_16', 3, NULL, 'assets/topic/asb/7608.png', 'Cat started running with the other animals.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_17', 3, NULL, 'assets/topic/asb/7609.png', 'Then they met the boy riding the bicycle and he asked, "Why are you running?'''' And the animals replied, "We don''t know. We heard what Rabbit said and she doesn''t know. She heard something falling and a voice said, ''Run rabbit run!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21140_18', 3, NULL, 'assets/topic/asb/7610.png', 'The boy laughed and said, "An apple fell off the tree because of the wind. I''m the one who said, ''Run Rabbit run!''"', NULL);
