INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2209_5', 3, NULL, 'assets/topic/asb/1549.png', 'It is windy.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2209_6', 3, NULL, 'assets/topic/asb/1550.png', 'It is cloudy.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2209_7', 3, NULL, 'assets/topic/asb/1551.png', 'It is cold.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2209_8', 3, NULL, 'assets/topic/asb/1552.png', 'It is raining.', NULL);
