INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751', 2, 'Sheep who got tired of city life', 'assets/topic/asb/26535.png', 'Mohammed Alhaji Modu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_3', 3, NULL, 'assets/topic/asb/26535.png', 'Sheep lived in the city. She got tired of city life and decided to go to the bush.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_4', 3, NULL, 'assets/topic/asb/26536.png', 'She greeted Hyena over and over again, "Brother Hyena, how are you? Brother Hyena, how are you?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_5', 3, NULL, 'assets/topic/asb/26537.png', 'When Sheep heard this, she started running.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_6', 3, NULL, 'assets/topic/asb/26538.png', 'Lion said to Hyena, "Can''t we even find peace in our dens? You throw dust into our eyes!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_7', 3, NULL, 'assets/topic/asb/26539.png', 'Lion said, "You are food to me, too, Hyena. Did I chase you? You came to me. Don''t blame me later."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_8', 3, NULL, 'assets/topic/asb/26540.png', 'Then Lion said to Sheep, "Why did you come to the bush?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_9', 3, NULL, 'assets/topic/asb/26541.png', 'Lion said, "Prepare traditional medicine to prove that you are truly a diviner." Sheep replied, "My medicine is expensive."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_10', 3, NULL, 'assets/topic/asb/26542.png', 'Sheep said, "The medicine is Hyena''s ear."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_11', 3, NULL, 'assets/topic/asb/26543.png', 'Lion ate Hyena''s ear and found it so sweet.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_12', 3, NULL, 'assets/topic/asb/26544.png', 'Sheep said, "Yes, it is possible my king." So Lion cut off Hyena''s second ear and brought it to Sheep.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_13', 3, NULL, 'assets/topic/asb/26545.png', 'Then Lion said to Sheep, "Is it possible to get the skin?" Sheep replied, "I think it is possible my king."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_14', 3, NULL, 'assets/topic/asb/26546.png', '"If such is life in the bush, then it is better for me to return home," said Sheep.', NULL);
