INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035', 2, 'Hen tricks Eagle', 'assets/topic/asb/9588.png', 'Nathan Higenyi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_3', 3, NULL, 'assets/topic/asb/9587.png', 'Long ago Hen and Eagle were friends. One day,  Eagle found Hen under a tree resting with her hopping bags. Hen was standing on one leg.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_4', 3, NULL, 'assets/topic/asb/9588.png', 'Eagle asked Hen,  "Where is your other leg?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_5', 3, NULL, 'assets/topic/asb/9589.png', 'Hen told Eagle,  "The shopkeeper cut off my leg and gave me these groceries in return."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_6', 3, NULL, 'assets/topic/asb/9590.png', 'Eagle asked Hen whether he could do the same. He also wanted a bag of groceries. Hen agreed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_7', 3, NULL, 'assets/topic/asb/9591.png', 'Eagle went to the shopkeeper and asked her to cut off one of his legs in exchange for groceries. The shopkeeper accepted.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_8', 3, NULL, 'assets/topic/asb/9592.png', 'The eagle hopped on one leg all the way home. But he was happy with the things the shopkeeper gave him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_9', 3, NULL, 'assets/topic/asb/9593.png', 'When Eagle reached home,  he found Hen with both her legs.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_10', 3, NULL, 'assets/topic/asb/9594.png', 'Eagle was furious and chased Hen. But he never caught her. Instead,  Eagle always looks out for Hen''s children. Whenever he catches one,  he eats it.', NULL);
