INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536', 2, 'Raven and Fox', 'assets/topic/asb/25729.png', 'Angesom Abadi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_3', 3, NULL, 'assets/topic/asb/25729.png', 'Long ago, wild animals lived together.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_4', 3, NULL, 'assets/topic/asb/25730.png', 'There was a famine. The animals had nothing to eat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_5', 3, NULL, 'assets/topic/asb/25731.png', 'Fox and Raven went in search of food.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_6', 3, NULL, 'assets/topic/asb/25732.png', 'Fox saw Raven eating meat alone. She grabbed the bird.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_7', 3, NULL, 'assets/topic/asb/25733.png', '"Please, do not kill me! Specially, do not throw me off the cliff," begged Raven.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_8', 3, NULL, 'assets/topic/asb/25734.png', 'Fox replied, "I will not throw you off the cliff."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_9', 3, NULL, 'assets/topic/asb/25735.png', 'But Fox had decided to throw Raven off the cliff.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_10', 3, NULL, 'assets/topic/asb/25736.png', 'Fox threw Raven off the cliff.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_11', 3, NULL, 'assets/topic/asb/25737.png', 'Meanwhile, Raven had turned in the air and started flying.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_12', 3, NULL, 'assets/topic/asb/25738.png', 'Raven landed in a tree.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_13', 3, NULL, 'assets/topic/asb/25739.png', '"I escaped because I am clever," boasted Raven.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_14', 3, NULL, 'assets/topic/asb/25740.png', '"You escaped because I let you escape," replied Fox.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
