INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149', 2, 'Pig that wouldn''t listen', 'assets/topic/asb/18310.png', 'Zanele Mguye
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_3', 3, NULL, 'assets/topic/asb/18306.png', 'After a long day at the farm,  it was time for all the animals to go to sleep.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_4', 3, NULL, 'assets/topic/asb/18307.png', '"Time for bed Miss Piggy," said the farmer. "In the pen you go."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_5', 3, NULL, 'assets/topic/asb/18308.png', '"Trusty Whip,  please whip Miss Piggy into the pen," said the farmer to her trusty whip.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_6', 3, NULL, 'assets/topic/asb/18309.png', '"Fiery Fire,  please burn Trusty Whip,  he won''t whip Miss Piggy into the pen," said the farmer to the fire.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_7', 3, NULL, 'assets/topic/asb/18310.png', '"Clear Water,  please put out Fiery Fire,  he won''t burn the Trusty Whip, and Miss Piggy won''t go into the pen," said the farmer to the water.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_8', 3, NULL, 'assets/topic/asb/18311.png', 'Then along came Milky Moo, the cow. "Milky Moo,  please drink Clear Water,  who won''t put out Fiery Fire. Fiery Fire won''t burn Trusty Whip, and Miss Piggy won''t go into the pen," said the farmer.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_9', 3, NULL, 'assets/topic/asb/18312.png', '"MOOO!" said thirsty Milky Moo with excitement.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_10', 3, NULL, 'assets/topic/asb/18313.png', '"No! No! No! Milky Moo,  please don''t drink me," cried Clear Water. "I will put out Fiery Fire."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_11', 3, NULL, 'assets/topic/asb/18314.png', '"Oh no you won''t put me out!" shouted Fiery Fire. "I will burn Trusty Whip."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_12', 3, NULL, 'assets/topic/asb/18315.png', '"Please don''t burn me Fiery Fire," cried Trusty Whip. "I will whip Miss Piggy into the pen."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_13', 3, NULL, 'assets/topic/asb/18316.png', 'Miss Piggy was annoyed. "There will be no need for you to whip me,  Trusty Whip," she said. "I will go into the pen."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21149_14', 3, NULL, 'assets/topic/asb/18317.png', 'Miss Piggy went into her pen and was ready for bed.', NULL);
