INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031', 2, 'Why Ajao was not buried', 'assets/topic/asb/16974.png', 'Taiwo Ẹhinẹni
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_3', 3, NULL, 'assets/topic/asb/16973.png', 'Ajao,  a bat,  lay in his house very ill. There was no one to attend to him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_4', 3, NULL, 'assets/topic/asb/16974.png', 'Then,  Ajao died.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_5', 3, NULL, 'assets/topic/asb/16975.png', 'The neighbours said,  "Ajao is dead. We must call his relations to come and bury him."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_6', 3, NULL, 'assets/topic/asb/16976.png', 'The neighbours went and called the birds saying,  "A relative of yours is dead."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_7', 3, NULL, 'assets/topic/asb/16977.png', 'The birds came and saw that it was Ajao. They said,  "This animal is not part of our family."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_8', 3, NULL, 'assets/topic/asb/16978.png', '"All our family have feathers but Ajao has none. So,  he is not one of us." The birds went away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_9', 3, NULL, 'assets/topic/asb/16979.png', 'The neighbours reasoned together. They agreed,  "The birds are right. Ajao has no feathers. So,  he is not their relative."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_10', 3, NULL, 'assets/topic/asb/16980.png', 'One of them said,  "He must be part of the family of rats. He looks like them."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_11', 3, NULL, 'assets/topic/asb/16981.png', 'The neighbours went and called the rats saying,  "One of your family is dead."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_12', 3, NULL, 'assets/topic/asb/16982.png', 'The rats came and saw that it was Ajao. They said,  "This animal is not part of our family."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_13', 3, NULL, 'assets/topic/asb/16983.png', '"Everyone in our family has a tail. Ajao has no tail." The rats went away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17031_14', 3, NULL, 'assets/topic/asb/16984.png', 'No relations were found for Ajao and he remained unburied.', NULL);
