INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938', 2, 'Why Hippos have no hair', 'assets/topic/asb/6143.png', 'Basilio Gimo
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_3', 3, NULL, 'assets/topic/asb/6162.png', 'One day,  Rabbit was walking by the riverside.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_4', 3, NULL, 'assets/topic/asb/6163.png', 'Hippo was there too,  going for a stroll and eating some nice green grass.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_5', 3, NULL, 'assets/topic/asb/6145.png', 'Hippo didn''t see that Rabbit was there and she accidentally stepped on Rabbit''s foot. Rabbit started screaming at Hippo,  "You Hippo! Can''t you see that you''re stepping on my foot?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_6', 3, NULL, 'assets/topic/asb/6146.png', 'Hippo apologised to Rabbit,  "I''m so sorry. I didn''t see you. Please forgive me!" But Rabbit wouldn''t listen and he shouted at Hippo,  "You did that on purpose! Someday,  you''ll see! You''re going to pay!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_7', 3, NULL, 'assets/topic/asb/6147.png', 'Rabbit went to find Fire and said,  "Go,  burn Hippo when she comes out of the water to eat grass. She stepped on me!"  Fire answered,  "No problem,  Rabbit,  my friend. I''ll do just what you ask."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_8', 3, NULL, 'assets/topic/asb/6148.png', 'Later,  Hippo was eating grass far from the river when,  "Whoosh!" Fire burst into flame. The flames began to burn Hippo''s hair.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_9', 3, NULL, 'assets/topic/asb/6149.png', 'Hippo started to cry and ran for the water. All her hair was burned off by the fire. Hippo kept crying,  "My hair has burned in the fire! My hair is all gone! My beautiful hair!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_10', 3, NULL, 'assets/topic/asb/6170.png', 'Rabbit was happy that Hippo''s hair was burned. And to this day,  for fear of fire,  the hippo never goes far from the water.', NULL);
