INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442', 2, 'Why is Yayeri great?', 'assets/topic/asb/7509.png', 'Cornelius Wambi Gulere
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_3', 3, NULL, 'assets/topic/asb/7513.png', 'Yayeri the great one wakes up very early every morning.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_4', 3, NULL, 'assets/topic/asb/7514.png', 'She is grateful for the night past.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_5', 3, NULL, 'assets/topic/asb/7517.png', 'She prepares her bed very well.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_6', 3, NULL, 'assets/topic/asb/7516.png', 'She greets her parents and grandparents.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_7', 3, NULL, 'assets/topic/asb/7515.png', 'She cleans her face with blue soap.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_8', 3, NULL, 'assets/topic/asb/7518.png', 'She brushes her teeth with the lughano stem.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_9', 3, NULL, 'assets/topic/asb/7507.png', 'She takes a cold water bath.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_10', 3, NULL, 'assets/topic/asb/7511.png', 'She puts on her orange school uniform.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_11', 3, NULL, 'assets/topic/asb/7508.png', 'She puts on her black shoes.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_12', 3, NULL, 'assets/topic/asb/7512.png', 'She hurries to her school at Mbala.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8442_13', 3, NULL, 'assets/topic/asb/7510.png', 'She studies Science,  Social Studies and Mathematics.
