INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080', 2, 'Lerato, Thabo and Cow', 'assets/topic/asb/9373.png', 'Khothatso Ranoosi and Marion Drew
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_3', 3, NULL, 'assets/topic/asb/9371.png', 'Once upon a time in a village there lived a boy named Thabo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_4', 3, NULL, 'assets/topic/asb/9370.png', 'Thabo was friends with a girl called Lerato. They were neighbours.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_5', 3, NULL, 'assets/topic/asb/9374.png', 'One day they went to get water.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_6', 3, NULL, 'assets/topic/asb/9373.png', 'When they were near to the spring,  they saw a cow lying there. They sat down and talked about how they could play with it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_7', 3, NULL, 'assets/topic/asb/9378.png', 'Then Thabo started to imitate the cow. "Muuu,  muuu,  muuuuuuu!" he said in a clear,  loud voice.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_8', 3, NULL, 'assets/topic/asb/9376.png', 'Until the cow turned to look at them. Suddenly Lerato got a fright. Thabo realised that she was scared.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_9', 3, NULL, 'assets/topic/asb/9380.png', 'Thabo said,  "Let''s go to this cow and hit it. It will run away and then we can get water." "Thabo,  I''m afraid!" said Lerato trembling.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_10', 3, NULL, 'assets/topic/asb/9381.png', 'Thabo went first. Lerato went slowly. They tried to hit the cow,  but it didn''t move.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_11', 3, NULL, 'assets/topic/asb/9385.png', 'Thabo said,  "Lerato,  you can stay here. I will fetch the water." Lerato agreed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_12', 3, NULL, 'assets/topic/asb/9384.png', 'Thabo scooped the water into the first bucket.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_13', 3, NULL, NULL, 'While he was scooping the water into the other bucket the cow stood up. She picked up Thabo with one horn,  and threw him onto her neck.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_14', 3, NULL, 'assets/topic/asb/9387.png', 'Thabo shouted,  "Joo,  jooo,  jooooo!" Lerato burst out laughing,  she could not stop.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_15', 3, NULL, 'assets/topic/asb/9388.png', 'The cow said,  "Mmuuu,  mmuuu,  muuuuuuuuuuu!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_16', 3, NULL, 'assets/topic/asb/9389.png', 'When at last Lerato stopped laughing,  she ran home as quickly as she could. When she arrived,  she found Ma Mathabo sweeping outside.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_17', 3, NULL, 'assets/topic/asb/9390.png', 'She said,  "Ma Ma Ma,  the co co cow has taken Thabo!" Lerato was panting.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22080_18', 3, NULL, 'assets/topic/asb/9391.png', 'Ma Mathabo dropped her broom and ran. What do you think happened next?', NULL);
